mapper.select:
SELECT


WHERE id IN

	#{id}

SELECT
data_party_rows,data_arch_point_rows,data_customer_tags_rows,data_login_rows,data_member_rows,data_payment_rows,data_population_rows,data_shopping_rows
FROM
(select count(id) as data_party_rows from data_party WHERE 1=1


	
			AND data_party.mobile is not null
	
	
			
	


		AND mapping_keyid IN
		
			#{mappingKeyId}
		
	


		AND data_party.create_time > #{timeCondition}

) as data_party_result,
(select count(id) as data_arch_point_rows from data_arch_point WHERE 1=1


	
			AND data_arch_point.mobile is not null
	
	
			AND data_arch_point.email is not null
	



		AND data_arch_point.create_time > #{timeCondition}

) as
data_arch_point,
(select count(id) as data_customer_tags_rows from data_customer_tags WHERE 1=1


	
			AND data_customer_tags.mobile is not null
	
	
			AND data_customer_tags.email is not null
	



		AND data_customer_tags.create_time > #{timeCondition}

) as
data_customer_tags,
(select count(id) as data_login_rows from data_login WHERE 1=1


	
			AND data_login.mobile is not null
	
	
			AND data_login.email is not null
	



		AND data_login.create_time > #{timeCondition}

) as data_login,
(select count(id) as data_member_rows from data_member WHERE 1=1


	
			AND data_member.mobile is not null
	
	
			AND data_member.email is not null
	



		AND data_member.create_time > #{timeCondition}

) as data_member,
(select count(id) as data_payment_rows from data_payment WHERE 1=1


	
			AND data_payment.mobile is not null
	
	
			AND data_payment.email is not null
	



		AND data_payment.create_time > #{timeCondition}

) as
data_payment,
(select count(id) as data_population_rows from data_population WHERE 1=1


	
			AND data_population.mobile is not null
	
	
			AND data_population.email is not null
	



		AND data_population.create_time > #{timeCondition}

) as
data_population,
(select count(id) as data_shopping_rows from data_shopping WHERE 1=1


	
			AND data_shopping.mobile is not null
	
	
			AND data_shopping.email is not null
	



) as data_shopping

