mapper.select:
SELECT
	COUNT(1)
FROM
	wechat_qrcode_focus
WHERE

	 DATE_FORMAT(focus_datetime,'%Y-%m-%d') = #{searchDate} 


	 DATE_FORMAT(unfocus_datetime,'%Y-%m-%d') = #{searchDate} 


	 DATE_FORMAT(focus_datetime,'%Y-%m-%d %H') = #{searchHours} 


	 DATE_FORMAT(unfocus_datetime,'%Y-%m-%d %H') = #{searchHours} 


	AND ch_code = #{chCode}


	AND wx_name = #{wxName}


	AND qrcode_id = #{qrcodeId}

AND #{fieldName} IS NOT NULL

		select 
		  	totalFocus.wx_acct AS wxAcct,
		  	totalFocus.wx_name AS wxName,
  			totalFocus.ch_code AS chCode,
  			wc.ch_name AS chName,
  			IFNULL(totalScan.value,0) AS totalScan,
  			IFNULL(totalScanUser.value,0) AS totalScanUser,
  			IFNULL(totalFocus.value,0) AS totalFocus,
  			IFNULL(newFocus.value,0) AS newFocus,
  			IFNULL(lostFocus.value,0) AS lostFocus 
		from
			(SELECT wx_acct AS wx_acct,wx_name AS wx_name,ch_code AS ch_code, COUNT(id) as value


		    	     AND wx_acct = #{wxAcct}
			    	 AND ch_code = #{chCode}
			    	 AND qrcode_id = #{qrcodeId}

			GROUP BY wx_acct,wx_name,ch_code) as totalFocus
			left join
			(SELECT wx_acct AS wx_acct,wx_name AS wx_name,ch_code AS ch_code, COUNT(id) as value


				and focus_datetime between #{startTime} and #{endTime}
		    	 AND wx_acct = #{wxAcct}
		    	 AND ch_code = #{chCode}
		    	 AND qrcode_id = #{qrcodeId}

			GROUP BY wx_acct,wx_name,ch_code) as newFocus
			ON totalFocus.wx_acct = newFocus.wx_acct AND totalFocus.ch_code = newFocus.ch_code 
			LEFT JOIN
			(SELECT wx_acct AS wx_acct,wx_name AS wx_name,ch_code AS ch_code, COUNT(id) as value


				and unfocus_datetime between #{startTime} and #{endTime}
		    	 AND wx_acct = #{wxAcct}
		    	 AND ch_code = #{chCode}
		    	 AND qrcode_id = #{qrcodeId}

			GROUP BY wx_acct,wx_name,ch_code) as lostFocus
			ON totalFocus.wx_acct = lostFocus.wx_acct AND totalFocus.ch_code = lostFocus.ch_code 

			LEFT JOIN
			(SELECT wx_acct AS wx_acct,wx_name AS wx_name,ch_code AS ch_code, COUNT(id) as value
			 FROM wechat_qrcode_scan 

				and create_time between #{startTime} and #{endTime}
		    	 AND wx_acct = #{wxAcct}
		    	 AND ch_code = #{chCode}
		    	 AND qrcode_id = #{qrcodeId}

			GROUP BY wx_acct,wx_name,ch_code) as totalScan
			ON totalFocus.wx_acct = totalScan.wx_acct AND totalFocus.ch_code = totalScan.ch_code
			LEFT JOIN
			(SELECT wx_acct AS wx_acct,wx_name AS wx_name,ch_code AS ch_code, COUNT(*) as value
			 FROM ( SELECT DISTINCT USER_ID, wx_acct AS wx_acct,wx_name AS wx_name,ch_code AS ch_code 
			     FROM wechat_qrcode_scan 

					and create_time between #{startTime} and #{endTime}
		    	     AND wx_acct = #{wxAcct}
		    		 AND ch_code = #{chCode}
		    		 AND qrcode_id = #{qrcodeId}

			GROUP BY wx_acct,wx_name,ch_code,USER_ID)  AS qrcodeScan  
				GROUP BY wx_acct,wx_name,ch_code) as totalScanUser
			ON totalFocus.wx_acct = totalScanUser.wx_acct AND totalFocus.ch_code = totalScanUser.ch_code
			LEFT JOIN wechat_channel wc ON wc.id = totalFocus.ch_code

SELECT DATE_FORMAT(focus_datetime,'%Y-%m-%d') as time, count(id) as value


    	 AND wx_acct = #{wxAcct}
    	 AND ch_code = #{chCode}
    	 AND qrcode_id = #{qrcodeId}

GROUP BY time order by value desc limit 1

SELECT DATE_FORMAT(focus_datetime,'%Y-%m-%d') as time, count(id) as value


		and focus_datetime between #{startTime} and #{endTime}
    	 AND wx_acct = #{wxAcct}
    	 AND ch_code = #{chCode}
    	 AND qrcode_id = #{qrcodeId}

GROUP BY time order by value desc limit 1

SELECT  newFocusData.newFocusTime as time, (IFNULL(newFocusData.newFocusValue,0) - IFNULL(lostFocusData.lostFocusValue,0)) value
from
(select DATE_FORMAT(focus_datetime,'%Y-%m-%d') as newFocusTime, count(id) as newFocusValue 


		and focus_datetime between #{startTime} and #{endTime}
    	 AND wx_acct = #{wxAcct}
    	 AND ch_code = #{chCode}
    	 AND qrcode_id = #{qrcodeId}

GROUP BY newFocusTime) as newFocusData

JOIN

(SELECT DATE_FORMAT(unfocus_datetime,'%Y-%m-%d') as lostFocusTime, count(id) as lostFocusValue


		and unfocus_datetime between #{startTime} and #{endTime}
    	 AND wx_acct = #{wxAcct}
    	 AND ch_code = #{chCode}
    	 AND qrcode_id = #{qrcodeId}

GROUP BY lostFocusTime)  as lostFocusData
on newFocusData.newFocusTime = lostFocusData.lostFocusTime
order by value desc limit 1

SELECT DATE_FORMAT(unfocus_datetime,'%Y-%m-%d') as time, count(id) as value


		and unfocus_datetime between #{startTime} and #{endTime}
    	 AND wx_acct = #{wxAcct}
    	 AND ch_code = #{chCode}
    	 AND qrcode_id = #{qrcodeId}

GROUP BY time order by value desc limit 1

SELECT qrcode_id


	    	 AND wx_name = #{wxName}
	    	 AND ch_code = #{chCode}
	    	and qrcode_id IS NOT NULL

GROUP BY qrcode_id

SELECT


WHERE qrcode_id = #{qrcodeId}
ORDER BY focus_datetime
