mapper.select:
select count(*) FROM
(
SELECT
	openid ,COUNT(1)
FROM
	wechat_qrcode_focus
WHERE 1=1

	 and DATE_FORMAT(focus_datetime,'%Y-%m-%d') = #{searchDate} 


	 and DATE_FORMAT(focus_datetime,'%Y-%m-%d %H') = #{searchHours} 


	AND ch_code = #{chCode}


	AND wx_name = #{wxName}


	AND qrcode_id = #{qrcodeId}

AND #{fieldName} IS NOT NULL
group by openid ) addcount

SELECT
	COUNT(1)
FROM
	wechat_qrcode_focus
WHERE 1=1 

	 and DATE_FORMAT(focus_datetime,'%Y-%m-%d') = #{searchDate} 


	 and DATE_FORMAT(focus_datetime,'%Y-%m-%d %H') = #{searchHours} 

 and focus_status='0' 

	AND ch_code = #{chCode}


	AND wx_name = #{wxName}


	AND qrcode_id = #{qrcodeId}

   SELECT total.time as time, count(*) as value from 
	(SELECT DATE_FORMAT(focus_datetime,'%Y-%m-%d') as time, openid,count(id) as value


	    	 AND wx_acct = #{wxAcct}
	    	 AND ch_code = #{chCode}
	    	 AND qrcode_id = #{qrcodeId}

	GROUP BY time,openid ) total		
	GROUP BY total.time order by value desc limit 1

    SELECT maxnew.time as time, count(*) as value from 
	(SELECT DATE_FORMAT(focus_datetime,'%Y-%m-%d') as time, openid,count(id) as value


			and focus_datetime between #{startTime} and #{endTime}
	    	 AND wx_acct = #{wxAcct}
	    	 AND ch_code = #{chCode}
	    	 AND qrcode_id = #{qrcodeId}

	GROUP BY time,openid ) maxnew	
	GROUP BY maxnew.time order by value desc limit 1

SELECT qrcode_id


	    	 AND wx_name = #{wxName}
	    	 AND ch_code = #{chCode}
	    	and qrcode_id IS NOT NULL

GROUP BY qrcode_id

SELECT


WHERE qrcode_id = #{qrcodeId}
ORDER BY focus_datetime
